package 연습장;

public class 메서드 {
	
	public static int sum(int n, int m) {
		return n+m;
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 20;
		int s;
		char a;
		
		s=sum(i,10);
		a='?';
		System.out.println(a);
		System.out.println("Hello");
		System.out.println(s);
		System.out.println("\\");
	}

}
